import React, { useState } from 'react'

const ProductCard = ({ product, addToCart }) => {
    const [quantity, setQuantity] = useState(1)
    const increment = () => setQuantity(quantity + 1)
    const decrement = () => { if (quantity > 1) setQuantity(quantity - 1) }

    const handleAddToCart = () => {
        addToCart(product, quantity);
    };

    return (
        <div className="max-w-xs rounded-lg bg-white shadow-lg overflow-hidden m-4 p-4">
            <h3 className="text-xl font-semibold text-gray-800 mb-2">{product.title}</h3>
            <p className="text-lg font-bold text-gray-600 mb-4">${product.price}</p>

            <div className="flex items-center space-x-2 mb-4">
                <button
                    className="w-8 h-8 bg-blue-500 text-white rounded-full flex items-center justify-center hover:bg-blue-600 focus:outline-none"
                    onClick={decrement}
                >
                    -
                </button>
                <input
                    type="number"
                    value={quantity}
                    readOnly
                    className="w-12 text-center bg-gray-100 border border-gray-300 rounded-lg"
                />
                <button
                    className="w-8 h-8 bg-blue-500 text-white rounded-full flex items-center justify-center hover:bg-blue-600 focus:outline-none"
                    onClick={increment}
                >
                    +
                </button>
            </div>

            <button
                onClick={handleAddToCart}
                className="w-full py-2 bg-blue-500 text-white font-bold rounded-lg hover:bg-blue-600 focus:outline-none"
            >
                Add to your cart!
            </button>
        </div>
    );
}

export default ProductCard;
